import React,{useEffect,useState} from "react";
import Question from "../../Components/Post/Question";
import api from '../../posts';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';


const QuestionPage = () => { 

    const [listQuestions,setlistQuestions] = useState(null);
    const [current_id,setCurrent_id] = useState(null);
    const [objcom, setObjcom] = useState({});

  useEffect(() => {
      fetch('http://localhost:5000/posts')
       .then(res => {
           return res.json();

      })
       .then(data => {
           setlistQuestions(data);

       })
   },[])

   const handleSubmit = async (e) => {
    e.preventDefault();
    const id = listQuestions.length ? listQuestions[listQuestions.length - 1].id + 1 : 1;;
    const newCom = { id, comments: comReplay };
    try {
      const response = await api.post('/posts/comments', newCom);

      const allPosts = [...listQuestions, response.data];

      setlistQuestions(allPosts);
      setComReplay('');
    } catch (err) {
      console.log(`Error: ${err.message}`);
    }
  }

    return(

        <>
        <List  sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }} >
            <ListItem alignItems="flex-start" >
        <div>
            <Question/>
            { listQuestions && listQuestions.map((users) => (
            <div>
            <h2> { users.id && users.body } </h2>
            {users.comments && users.comments.map((com) => (
                <p>{com.comment &&  setObjcom({...objcom,  comment: {
                    com_id: com.com_id, user_id: com.user_id, comment: com.comment

                    },
               })}</p> ))}

        <form className="newPostForm" onSubmit={handleSubmit}>

                <label htmlFor="postBody">Comment:</label>
                <Typography
                    id="postBody"
                    required
                    onChange={(e) => { setComReplay(e.target.value); setCurrent_id(users.id) }}
                />
                <button type="submit">Submit</button>
            </form>
</div>
            )) }
        </div>
        </ListItem>
        </List>


        </>

    )

}






export default QuestionPage;
